sect3.programlisting:
case parametro in
        schema1[|schema1a]) lista1 di comandi;;
        schema2) lista2 di comandi
                 lista2a di comandi;;
        schema3) lista3 di comandi;;
        *) ;;
esac

#!/bin/sh
case $1 in
        aa|ab) echo A
        ;;
        b?)    echo "B \c"
               echo $1;;
        c*)    echo C;;
        *)     echo D;;
esac
