sect2.programlisting:
@rands = ();
foreach (0..4) {
	push(@rands, rand 64);
}

$salt = join '', ('.', '/', 0..9, 'A'..'Z', 'a'..'z')[@rands];

$salt = '$1$' . $salt . '$';

print 'Enter password: ';
`stty -echo`;
$cleartext = <>;
`stty echo`;
chop($cleartext);
print crypt($cleartext, $salt), "\n";

#!/bin/sh # # Startup for conserver #
PATH=/usr/bin:/usr/local/bin
case "$1" in 'start') TTY=/dev/cuaE7 conserver -d > $TTY # get NL->CR+NL mapping so msgs look right stty < /dev/cuaE7 opost onlcr echo -n ' conserver' ;;
'stop') kill `cat /var/run/conserver.pid` && echo -n ' conserver' ;;
*) echo "Usage: $0 { start | stop }" ;;
esac exit 0
